head.script:
//
// Fetch API polyfill for older browsers (especially IE)
if (!window.fetch) {
  document.write('<script src="https://cdn.jsdelivr.net/npm/whatwg-fetch@3.6.2/dist/fetch.umd.min.js"><\/script>');
}

// CustomEvent polyfill for older browsers
(function() {
  if (typeof window.CustomEvent === 'function') return;

  function CustomEvent(event, params) {
    params = params || { bubbles: false, cancelable: false, detail: null };
    var evt = document.createEvent('CustomEvent');
    evt.initCustomEvent(event, params.bubbles, params.cancelable, params.detail);
    return evt;
  }

  window.CustomEvent = CustomEvent;
})();
//

body.b:template-script:
//
  // Blogger template script
  function setAttributeOnload(object, attribute, val) {
    if(window.addEventListener) {
      window.addEventListener('load',
        function(){ object[attribute] = val; }, false);
    } else {
      window.attachEvent('onload', function(){ object[attribute] = val; });
    }
  }
//

body.script:
//
document.addEventListener('DOMContentLoaded', function() {
  // Check for saved dark mode preference
  const darkModeEnabled = localStorage.getItem('darkMode') === 'true';

  // Create dark mode toggle button
  const darkModeToggle = document.createElement('button');
  darkModeToggle.className = 'dark-mode-toggle' + (darkModeEnabled ? ' active' : '');
  darkModeToggle.setAttribute('aria-label', 'Toggle dark mode');
  darkModeToggle.innerHTML = '<span class="dark-mode-icon">🌙</span><span class="light-mode-icon">☀️</span>';
  document.body.appendChild(darkModeToggle);

  // Apply dark mode if enabled
  if (darkModeEnabled) {
    document.documentElement.setAttribute('data-theme', 'dark');
  }

  // Toggle dark mode on click
  darkModeToggle.addEventListener('click', function() {
    const isDarkMode = document.documentElement.getAttribute('data-theme') === 'dark';

    if (isDarkMode) {
      document.documentElement.removeAttribute('data-theme');
      darkModeToggle.classList.remove('active');
      localStorage.setItem('darkMode', 'false');
    } else {
      document.documentElement.setAttribute('data-theme', 'dark');
      darkModeToggle.classList.add('active');
      localStorage.setItem('darkMode', 'true');
    }
  });
});
//

//
document.addEventListener('DOMContentLoaded', function() {
  const mobileMenuToggle = document.querySelector('.mobile-menu-toggle');
  const navMenu = document.querySelector('.nav-menu');

  if (mobileMenuToggle && navMenu) {
    mobileMenuToggle.addEventListener('click', function() {
      const isExpanded = navMenu.classList.contains('active');
      navMenu.classList.toggle('active');
      mobileMenuToggle.setAttribute('aria-expanded', !isExpanded);

      // Close menu when clicking outside
      if (!isExpanded) {
        document.addEventListener('click', function closeMenu(e) {
          if (!navMenu.contains(e.target) && !mobileMenuToggle.contains(e.target)) {
            navMenu.classList.remove('active');
            mobileMenuToggle.setAttribute('aria-expanded', 'false');
            document.removeEventListener('click', closeMenu);
          }
        });
      }
    });

    // Close menu on escape key
    document.addEventListener('keydown', function(e) {
      if (e.key === 'Escape' && navMenu.classList.contains('active')) {
        navMenu.classList.remove('active');
        mobileMenuToggle.setAttribute('aria-expanded', 'false');
      }
    });
  }
});
//

//
document.addEventListener('DOMContentLoaded', function() {
  // Debug mode - set to true to enable console logging
  const DEBUG = false;

  // Helper function for logging
  function log(message, data) {
    if (DEBUG) {
      console.log('[Kanahola Load More]', message, data || '');
    }
  }

  log('Initializing Load More Posts functionality');

  // Variables using native JavaScript selectors
  const postGrid = document.querySelector('.post-grid');
  const loadMoreButton = document.getElementById('load-more-posts');
  const loader = document.querySelector('.infinite-scroll-loader');
  let isLoading = false;

  // Check if elements exist
  if (!postGrid) log('Warning: Post grid element not found');
  if (!loadMoreButton) log('Warning: Load more button not found');
  if (!loader) log('Warning: Loader element not found');

  // Hide the button if there's no older posts link
  const olderPostsLink = document.querySelector('#blog-pager-older-link a');
  if (!olderPostsLink) {
    loadMoreButton.style.display = 'none';
  }

  // Load more posts on button click
  loadMoreButton.addEventListener('click', function() {
    if (isLoading) return;

    // Get the URL of the next page
    const olderPostsLink = document.querySelector('#blog-pager-older-link a');
    const nextPageUrl = olderPostsLink ? olderPostsLink.getAttribute('href') : null;

    if (!nextPageUrl) {
      loadMoreButton.disabled = true;
      loadMoreButton.textContent = 'No More Posts';
      return;
    }

    // Show loading indicator
    isLoading = true;
    loader.classList.add('active');
    loadMoreButton.disabled = true;

    // Use standard browser navigation instead of AJAX to maintain Blogger compatibility
    window.location.href = nextPageUrl;
  });

  // Add support for keyboard navigation
  loadMoreButton.addEventListener('keydown', function(e) {
    if (e.key === 'Enter' || e.key === ' ') {
      e.preventDefault();
      loadMoreButton.click();
    }
  });
});
//
